import styled from "styled-components";
import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import noImage from "../../img/noImage.jpg";
import Card from "react-bootstrap/Card";

interface DataType {
  name: string;
  product_image: string;
  price: string;
  body: any;
  title: any;
  basicUserInfoResponse: any;
  contents: any;
  createdTime: any;
}

interface RenderProps {
  currPageNum: number;
  renderData: any;
}

function RenderContent({ renderData, currPageNum }: RenderProps) {
  const [pageData, setPageData] = useState([]);

  // useEffect(() => {
  //   window.scrollTo(0, 0);
  //   let firstPageNum = currPageNum * 16;
  //   let lastPageNum = firstPageNum + 15;
  //   let getFilter = renderData.filter((el: any, idx: number) => {
  //     return firstPageNum <= idx && lastPageNum >= idx;
  //   });
  //   setPageData(getFilter);
  // }, [renderData, currPageNum]);
  //
  // const handleOnErrorImg = (
  //   e: React.SyntheticEvent<HTMLImageElement, Event>
  // ) => {
  //   e.currentTarget.src = noImage;
  // };

  return (
    <>
      {renderData.length === 0 ? (
        <Warnning>게시물이 비어있습니다.</Warnning>
      ) : (
        <ContentContainer>
          {renderData.map((el: DataType, idx: number) => {
            return (
              <CardContainer>
                <Card style={{ width: "18rem" }}>
                  <Card.Body>
                    <Card.Title>{el.title}</Card.Title>
                    <Card.Text>{el.contents}</Card.Text>
                    <div> {el.basicUserInfoResponse.userName}</div>
                    <div> {el.createdTime}</div>
                    <Button size="sm" variant="primary"></Button>
                  </Card.Body>
                </Card>
              </CardContainer>
            );
          })}
        </ContentContainer>
      )}
    </>
  );
}

export default RenderContent;

const ContentContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: start;
  flex-wrap: wrap;
  width: 100%;
`;

const ProductImg = styled.div`
  width: 243px;
  height: 210px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Img = styled.img`
  width: 100%;
  height: 100%;
`;

const ProductEle = styled.div`
  flex: 1 1;
  width: 243px;
  height: 310px;
  margin-right: 11px;
  margin-bottom: 11px;
  background-color: rgb(255, 255, 255);
  border: 1px solid rgb(238, 238, 238);
  cursor: pointer;
`;

const ProductInfo = styled.div`
  padding: 15px 10px;
  height: 80px;
`;

const Warnning = styled.div`
  height: 50vh;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
`;

const Title = styled.div`
  width: 100%;
  font-weight: 400;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  padding-bottom: 20px;
`;

const Price = styled.div`
  font-weight: bold;
`;

const CardContainer = styled.div`
  cursor: pointer;
  &:hover {
    text: bold;
  }
`;
