import styled from "styled-components";
import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import noImage from "../../img/noImage.jpg";
import Card from "react-bootstrap/Card";
import { useNavigate } from "react-router-dom";

interface DataType {
  name: string;
  product_image: string;
  price: string;
  body: any;
  title: any;
  basicUserInfoResponse: any;
  contents: any;
  createdTime: any;
}

interface RenderProps {
  currPageNum: number;
  renderData: any;
}

function RenderContent({ renderData, currPageNum }: RenderProps) {
  const [pageData, setPageData] = useState([]);

  const navigate = useNavigate();

  // useEffect(() => {
  //   window.scrollTo(0, 0);
  //   let firstPageNum = currPageNum * 16;
  //   let lastPageNum = firstPageNum + 15;
  //   let getFilter = renderData.filter((el: any, idx: number) => {
  //     return firstPageNum <= idx && lastPageNum >= idx;
  //   });
  //   setPageData(getFilter);
  // }, [renderData, currPageNum]);
  //
  // const handleOnErrorImg = (
  //   e: React.SyntheticEvent<HTMLImageElement, Event>
  // ) => {
  //   e.currentTarget.src = noImage;
  // };

  const HandleCard = (data: DataType) => {
    navigate("/detail/feed", { state: data });
  };

  return (
    <>
      {renderData.length === 0 ? (
        <Warnning>게시물이 비어있습니다.</Warnning>
      ) : (
        <div className="row row-cols-5 row-cols-md-4 g-4">
          {renderData.map((el: DataType, idx: number) => {
            return (
              <div className="col" onClick={() => HandleCard(el)}>
                <div className="card baseBlock">
                  <div className="card-body">
                    <h5 className="card-title">{el.title}</h5>
                    <h6 className="card-subtitle mb-2 text-body-secondary">{el.basicUserInfoResponse.userName}</h6>
                    <p className="card-text">{el.contents}</p>
                    <SubInfo>
                      <SubEventDate> 2024.01.01 </SubEventDate>
                      <div>
                        <SubEvent className="bi bi-hand-thumbs-up fs-6"> 2 </SubEvent>
                        <SubEvent className="bi bi-chat-dots-fill">10</SubEvent>
                      </div>
                    </SubInfo>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </>
  );
}

export default RenderContent;

const ProductImg = styled.div`
  width: 243px;
  height: 210px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Img = styled.img`
  width: 100%;
  height: 100%;
`;

const ProductEle = styled.div`
  flex: 1 1;
  width: 243px;
  height: 310px;
  margin-right: 11px;
  margin-bottom: 11px;
  background-color: rgb(255, 255, 255);
  border: 1px solid rgb(238, 238, 238);
  cursor: pointer;
`;

const ProductInfo = styled.div`
  padding: 15px 10px;
  height: 80px;
`;

const Warnning = styled.div`
  height: 50vh;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
`;

const Title = styled.div`
  width: 100%;
  font-weight: 400;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  padding-bottom: 20px;
`;

const Price = styled.div`
  font-weight: bold;
`;

const SubInfo = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const SubEventDate = styled.div`
  margin-bottom: 4px;
`;
const SubEvent = styled.span`
  display: inline-block;
  margin-left: 15px;
`;
