            <ModalWrapper isOpen={isOpen}>
                <ModalContent>
                    <CloseButton onClick={closeModal}>&times;</CloseButton>
                    <p>모달 내용이 여기에 들어갑니다.</p>
                </ModalContent>
            </ModalWrapper>
        </div>
    )
}


export default LoginModal;
