import styled from "styled-components";
import Comment from "../comment/Comment";

function DetailFeedBody() {
  return (
    <BodyContainer>
      <BodyWrapper>
        <MainSubTitle>게시글 보기</MainSubTitle>
        <TitleContainer>
          <h3>오늘 너무 신나는 날이였음</h3>
          <SubtitleContainer>
            <SubTitle>jun kim</SubTitle>
            <SubTitle>2024-01-01</SubTitle>
          </SubtitleContainer>
        </TitleContainer>

        <ContentContainer>사람들 즐거워보임</ContentContainer>

        <LikesBtn>
          <i className="bi bi-hand-thumbs-up fs-3"></i>
        </LikesBtn>

        <CommentContainer>
          <Comment />
        </CommentContainer>
      </BodyWrapper>
    </BodyContainer>
  );
}

export default DetailFeedBody;

const BodyContainer = styled.div`
  width: 100%;
`;

const BodyWrapper = styled.div`
  width: 900px;
  margin: auto;
  padding: 30px 0;
`;

const TitleContainer = styled.div`
  margin-top: 30px;
`;

const ContentContainer = styled.div`
  margin-top: 50px;
`;

const SubtitleContainer = styled.div`
  margin-top: 30px;
  text-align: right;
`;

const SubTitle = styled.span`
  display: inline-block;
  padding-left: 20px;
`;

const CommentContainer = styled.div`
  margin-top: 100px;
`;

const MainSubTitle = styled.div`
  font-size: 12px;
  border-bottom: 1px solid rgb(238, 238, 238);
  padding-bottom: 9px;
  margin-bottom: 9px;
`;

const LikesBtn = styled.div`
  text-align: right;
  font-size: 10px;
`;
