import styled from "styled-components";
import Comment from "../comment/Comment";
import axios from "axios";
import dayjs from "dayjs";

import { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";

function DetailFeedBody() {
  const { state } = useLocation();
  console.log(state);
  const [page, setPage] = useState(0);
  const [title, setTitle] = useState(state.title);
  const [writer, setWriter] = useState(state.basicUserInfoResponse.userName);
  const [body, setBody] = useState(state.contents);
  const [date, setDate] = useState(dayjs(state.createdTime).format("YYYY-MM-DD HH:mm"));
  const [id, setId] = useState(state.id);

  const [likes, setLikes] = useState(0);
  const [comments, setComments] = useState([]);
  const [totalPage, setTotalPage] = useState(0);
  const [comment, setComment] = useState();

  //   const handleLikePost = (event) => {
  //     console.log(localStorage.getItem("token"));
  //     axios({
  //       url: "/api/v1/favorite/board/" + id,
  //       method: "POST",
  //       headers: {
  //         Authorization: "Bearer " + localStorage.getItem("token"),
  //       },
  //     })
  //       .then((res) => {
  //         console.log("success");
  //         handleLikeCounts();
  //       })
  //       .catch((error) => {
  //         console.log(error);
  //       });
  //   };

  //   const handleLikeCounts = (event) => {
  //     console.log(localStorage.getItem("token"));
  //     axios({
  //       url: "/api/v1/favorite/board/" + id,
  //       method: "GET",
  //       headers: {
  //         Authorization: "Bearer " + localStorage.getItem("token"),
  //       },
  //     })
  //       .then((res) => {
  //         console.log("success");
  //         setLikes(res.data.result);
  //       })
  //       .catch((error) => {
  //         console.log(error);
  //       });
  //   };

  //   const changePage = (pageNum) => {
  //     console.log("change pages");
  //     console.log(pageNum);
  //     console.log(page);
  //     setPage(pageNum);
  //     handleGetComments(pageNum);
  //   };

  const handleGetComments = (pageNum, event) => {
    console.log("handleGetComments");
    axios({
      // url: '/api/v1/board/' + id + '/comments?size=5&sort=id&page=' + pageNum,
      url: "/api/v1/user/board/" + id + "/comment",
      method: "GET",
      headers: {
        Authorization: "Bearer " + localStorage.getItem("token"),
      },
    })
      .then((res) => {
        console.log("success comment");
        console.log(res);
        setComments(res.data.responseBody);
        setTotalPage(res.data.boardGetResponse.totalPages);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleWriteComment = (pageNum, event) => {
    console.log("handleWriteComment");
    axios({
      url: "/api/v1/user/board/" + id + "/comment",
      method: "POST",
      headers: {
        Authorization: "Bearer " + localStorage.getItem("token"),
      },
      data: {
        content: comment,
      },
    })
      .then((res) => {
        console.log("success");
        handleGetComments();
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    handleGetComments();
    // handleLikeCounts();
  }, "");

  return (
    <BodyContainer>
      <BodyWrapper>
        <MainContent>
          <MainSubTitle>게시글 보기</MainSubTitle>
          <TitleContainer>
            <h3> {title}</h3>
            <SubtitleContainer>
              <SubTitle> {writer}</SubTitle>
              <SubTitle>{date}</SubTitle>
            </SubtitleContainer>
          </TitleContainer>

          <ContentContainer>{body}</ContentContainer>

          <LikesBtn>
            <p className="bi bi-hand-thumbs-up fs-6"> {likes} </p>
          </LikesBtn>
        </MainContent>
        <CommentContainer>
          <MainSubTitle>댓글</MainSubTitle>

          {comments.map((comment) => (
            <CommentsData>
              <span>
                <i class="bi bi-person-circle"></i>
              </span>

              <span> {comment.userInfo.userName}</span>
              <div> {comment.commentInfo.content}</div>
              <div>{comment.body}</div>
            </CommentsData>
          ))}

          <CommentContainer>
            <form className="form-group  mb-2 row">
              <div className="form-group col">
                <textarea className="form-control" id="inputPassword2" placeholder="댓글 추가" onChange={(v) => setComment(v.target.value)} />
              </div>
              <button type="button" className="btn btn-primary col-1" onClick={handleWriteComment}>
                등록
              </button>
            </form>
          </CommentContainer>
        </CommentContainer>
      </BodyWrapper>
    </BodyContainer>
  );
}

export default DetailFeedBody;

const BodyContainer = styled.div`
  width: 100%;
`;

const BodyWrapper = styled.div`
  width: 900px;
  margin: auto;
  padding: 30px 0;
`;

const TitleContainer = styled.div`
  margin-top: 30px;
`;

const ContentContainer = styled.div`
  margin-top: 50px;
`;

const SubtitleContainer = styled.div`
  margin-top: 30px;
  text-align: right;
`;

const SubTitle = styled.span`
  display: inline-block;
  padding-left: 20px;
`;

const CommentContainer = styled.div`
  margin-top: 50px;
`;

const CommentsData = styled.div`
  padding: 20px 0;
  border-bottom: 1px solid rgb(238, 238, 238);
`;

const MainSubTitle = styled.div`
  font-size: 12px;
  border-bottom: 1px solid rgb(238, 238, 238);
  padding-bottom: 9px;
  margin-bottom: 9px;
`;

const LikesBtn = styled.div`
  margin-top: 40px;
  font-size: 6px;
  text-align: right;
`;

const MainContent = styled.div`
  //   border-bottom: 1px solid rgb(238, 238, 238);
  //   padding-bottom: 40px;
`;
