import styled from "styled-components";
import { useState, useEffect } from "react";
import axios from "axios";
import { useAppSelector } from "../../hooks";
import Pagination from "../Pagination";
import RenderContent from "./RenderContent";
import BodyTitle from './BodyTitle';

function Body() {
  const { searchWord, orderCommand } = useAppSelector(
    (state) => state.searchState
  );
  const [renderData, setRendering] = useState([]);
  const [currPageNum, setCurrPageNum] = useState(0);
  const [totalPageNum, setTotalPageNum] = useState<Array<number>>([]);

  useEffect(() => {
    const getUpdateData = async () => {
      let apiData = await axios.get(
        `https://api.stg-bunjang.co.kr/api/1/find_v2.json?q=${searchWord}&order=${orderCommand}&version=4`
      );
      setRendering(apiData.data.list);
      setCurrPageNum(0);
      let totalPage = [];

      for (let i = 0; i < Math.ceil(apiData.data.list.length / 16); i++) {
        totalPage.push(i);
      }

      setTotalPageNum(totalPage);
    };

    getUpdateData();
  }, [orderCommand, searchWord]);

  return (
    <BodyContainer>
      <BodyWrapper>
        <BodyTitle renderData={renderData} />
        <RenderContent renderData={renderData} currPageNum={currPageNum} />
        <Pagination
          setCurrPageNum={setCurrPageNum}
          totalPageNum={totalPageNum}
          currPageNum={currPageNum}
        />
      </BodyWrapper>
    </BodyContainer>
  );
}

export default Body;

const BodyContainer = styled.div`
  width: 100%;
`;

const BodyWrapper = styled.div`
  width: 1024px;
  margin: auto;
  padding: 30px 0;
`;
