import styled from "styled-components";
import React, { useState, useEffect } from "react";
import noImage from "../../img/noImage.jpg";

interface DataType {
  name: string;
  product_image: string;
  price: string;
}

interface RenderProps {
  currPageNum: number;
  renderData: any;
}

function RenderContent({ renderData, currPageNum }: RenderProps) {
  const [pageData, setPageData] = useState([]);

  useEffect(() => {
    window.scrollTo(0, 0);
    let firstPageNum = currPageNum * 16;
    let lastPageNum = firstPageNum + 15;
    let getFilter = renderData.filter((el: any, idx: number) => {
      return firstPageNum <= idx && lastPageNum >= idx;
    });
    setPageData(getFilter);
  }, [renderData, currPageNum]);

  const handleOnErrorImg = (
    e: React.SyntheticEvent<HTMLImageElement, Event>
  ) => {
    e.currentTarget.src = noImage;
  };

  return (
    <>
      {renderData.length === 0 ? (
        <Warnning>게시물이 비어있습니다.</Warnning>
      ) : (
        <ContentContainer>
          {pageData.map((el: DataType, idx: number) => {
            return (
              <ProductEle key={idx}>
                <ProductImg>
                  <Img src={el.product_image} onError={handleOnErrorImg} />
                </ProductImg>
                <ProductInfo>
                  <Title>{el.name}</Title>
                  <Price>{el.price} 원</Price>
                </ProductInfo>
              </ProductEle>
            );
          })}
        </ContentContainer>
      )}
    </>
  );
}

export default RenderContent;

const ContentContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: start;
  flex-wrap: wrap;
  width: 100%;
`;

const ProductImg = styled.div`
  width: 243px;
  height: 210px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Img = styled.img`
  width: 100%;
  height: 100%;
`;

const ProductEle = styled.div`
  flex: 1 1 1;
  width: 243px;
  height: 310px;
  margin-right: 11px;
  margin-bottom: 11px;
  background-color: rgb(255, 255, 255);
  border: 1px solid rgb(238, 238, 238);
  cursor: pointer;
`;

const ProductInfo = styled.div`
  padding: 15px 10px;
  height: 80px;
`;

const Warnning = styled.div`
  height:50vh;
  display:flex;
  align-items:center;
  justify-content:center;
  font-weight:bold;
`;

const Title = styled.div`
  width: 100%;
  font-weight: 400;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  padding-bottom: 20px;
`;

const Price = styled.div`
  font-weight: bold;
`;
